const CommonUtils =  {
    stringToPascalCase: (text) => {
        return text
        // insert a space before all caps
        .replace(/([A-Z])/g, ' $1')
        // uppercase the first character
        .replace(/^./, function(str){ return str.toUpperCase(); })
    }
}

export default CommonUtils;
