import { createSlice } from "@reduxjs/toolkit";


const covidSlice = createSlice({
    name: "Covid Slice",
    initialState: {
        covidStatus: {
            confirmed: 0,
            recovered: 0,
            deaths: 0,
        },
        covidStatus2: [
            {
                status: "Positif",
                total: 6012818,
                detail: "https://covid19.mathdro.id/api/countries/indonesia/confirmed",
              },
              {
                status: "Sembuh",
                total: 5750802,
                detail: "https://covid19.mathdro.id/api/countries/indonesia/recovered",
              },
              {
                status: "Meninggal",
                total: 155089,
                detail: "https://covid19.mathdro.id/api/countries/indonesia/deaths",
              },
        ],
    },
    reducers: {
        setCovidStatus(state, action){
            state.covidStatus = action.payload;
        }
    }
})

const covidReducer = covidSlice.reducer;
const { setCovidStatus } = covidSlice.actions;

export default covidReducer;
export {
    setCovidStatus
};
