import {
    createSlice
} from "@reduxjs/toolkit";


const covidSlice = createSlice({
    name: "Covid Slice",
    initialState: {
        covidStatus: {
            confirmed: {
                value: 0,
                detail: ""
            },
            recovered: {
                value: 0,
                detail: ""
            },
            deaths: {
                value: 0,
                detail: ""
            },
        },
    },
    reducers: {
        setConfirmedCovidStatus(state, action) {
            state.covidStatus = action.payload;
        },
        setRecoveredCovidStatus(state, action) {
            state.covidStatus = action.payload;
        },
    }
})

const covidReducer = covidSlice.reducer;
const {
    setCovidStatus
} = covidSlice.actions;

export default covidReducer;
export {
    setCovidStatus
};
