import CardGlobal from "../CardGlobal/CardGlobal"
import data from "../../utils/constants/indonesia"
import GlobalCovidStyled from "./GlobalCovidStyled"
import CommonUtils from "../../utils/common_utils"
import {
    useSelector
} from "react-redux"

function GlobalCovid(props) {

    const covidStatus = useSelector(store => store.covidStatus.covidStatus);
    console.log(covidStatus);

    const listGlobalCovidStatus = data.indonesia
    const {
        type
    } = props;

    const listGlobalCovidStatus2 = [{
            status: "Positif",
            total: 6012818,
            detail: "https://covid19.mathdro.id/api/countries/indonesia/confirmed",
        },
        {
            status: "Sembuh",
            total: 5750802,
            detail: "https://covid19.mathdro.id/api/countries/indonesia/recovered",
        },
        {
            status: "Meninggal",
            total: 155089,
            detail: "https://covid19.mathdro.id/api/countries/indonesia/deaths",
        },
    ];

    return ( <
        GlobalCovidStyled >
        <
        div className = {
            `container`
        } >
        <
        section >
        <
        div className = {
            `title_container`
        } >
        <
        h1 > {
            CommonUtils.stringToPascalCase(type)
        } < /h1> <
        p > Data Covid Berdasarkan {
            CommonUtils.stringToPascalCase(type)
        } < /p> <
        /div> <
        div className = {
            `grid_container`
        } > {
            listGlobalCovidStatus.map(function (globalStatus) {
                return <CardGlobal status = {
                    globalStatus.status
                }
                jumlah = {
                    globalStatus.total
                }
                key = {
                    globalStatus.status
                }
                />
            })
        } {
            /* <CardGlobal status={"Sembuh"} jumlah={6005646} />
                                    <CardGlobal status={"Dirawat"} jumlah={100000} />
                                    <CardGlobal status={"Meninggal"} jumlah={10000} /> */
        } <
        /div> <
        /section> <
        /div> <
        /GlobalCovidStyled>
    )
}

export default GlobalCovid
