import styled from "styled-components";

const HeroStyled = styled.div`
    .container {
        margin: 1rem;
        background: red;
    }

    .hero {
        display: flex;
        flex-direction: column;
        text-align: center;
    }

    .hero__left {
        margin-bottom: 1rem;
    }

    .hero__title {
        color: #06D6A0;
        margin-bottom: 1rem;
        font-size: 2.44rem;
    }

    .hero__subtitle {
        color: #118AB2;
        font-size: 1.59rem;
    }

    .hero__description {
        color: #64748b;
        margin-bottom: 1rem;
    }

    .hero__button {
        padding: 0.8rem 2rem;
        border: none;
        border-radius: 10px;
        background-color: #06D6A0;
        color: #fff;
    }

    .hero__image {
        max-width: 100%;
        height: auto;
        border-radius: 25px;
    }
`

export default HeroStyled;
